import UIKit

enum CryptoModels {
    // MARK: Use cases
    
    enum FetchCrypto {
        struct Request {
            var ticket: String
            var currency: String
        }
        struct Response: Codable {
            var time, assetIdBase, assetIdQuote: String?
            var rate: Double?
            enum CodingKeys: String, CodingKey {
                case time
                case assetIdBase
                case assetIdQuote
                case rate
            }
        }
        struct ViewModel {
            struct DisplayedCrypto {
                var price: String
                var ticket: String
            }
            var displayedCrypto: DisplayedCrypto
        }
    }
}
